// Fetch media statistics from APIs
setInterval(() => {
  // This would connect to your actual APIs
  document.getElementById('movie-count').textContent = Math.floor(Math.random() * 5000);
  document.getElementById('tv-count').textContent = Math.floor(Math.random() * 500);
  document.getElementById('music-count').textContent = Math.floor(Math.random() * 1000);
