customScripts.systemHealth:
// Update system health metrics
setInterval(() => {
  const cpuEl = document.querySelector('.health-item:nth-child(1) .health-value');
  const ramEl = document.querySelector('.health-item:nth-child(2) .health-value');
  const tempEl = document.querySelector('.health-item:nth-child(3) .health-value');

  if (cpuEl) cpuEl.textContent = Math.floor(Math.random() * 100) + '%';
  if (ramEl) ramEl.textContent = Math.floor(Math.random() * 100) + '%';
  if (tempEl) tempEl.textContent = Math.floor(Math.random() * 30 + 40) + '°C';
}, 3000);
